import React, { useState } from 'react';
import './LoginSignup.css';
import axios from 'axios';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

// LostPassword Component
export const LostPassword = ({ onBackToLogin }) => {
    const [email, setEmail] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Request to reset password for:', email);
        // Implementation for sending a password reset request goes here

        setEmail(''); // Clear the input field
        alert('If an account with that email was found, we sent an email to reset your password.');
        onBackToLogin(); // Switch back to the login view
    };

    return (
        <div className="lost-password-container">
            <h2>Reset Password</h2>
            <form onSubmit={handleSubmit}>
                <div className="input">
                    <img src={email_icon} alt="Email" />
                    <input
                        type="email"
                        placeholder="Enter your email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Send Reset Link</button>
                <button type="button" onClick={onBackToLogin}>Back to Login</button>
            </form>
        </div>
    );
};

// LoginSignup Component
const LoginSignup = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [action, setAction] = useState("Sign Up");
    const [showLostPassword, setShowLostPassword] = useState(false);

    if (showLostPassword) {
        return <LostPassword onBackToLogin={() => setShowLostPassword(false)} />;
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://your-php-server/mongodb.php', { email, password });
            setMessage(response.data.message);
        } catch (error) {
            console.error('Error:', error);
            setMessage('An error occurred. Please try again.');
        }
    };

    return (
        <div className='container'>
            <div className="header">
                <div className='text'>{action}</div>
                <div className="underline"></div>      
            </div>
            <div className='inputs'>
                {action === "Sign Up" && (
                    <div className='input'>
                        <img src={user_icon} alt="" />
                        <input type="text" placeholder='Name'/>   
                    </div>
                )}
                <div className='input'>
                    <img src={email_icon} alt="" />
                    <input type="email" placeholder='Email Id'/>   
                </div>
                <div className='input'>
                    <img src={password_icon} alt="" />
                    <input type="password" placeholder='Password'/>   
                </div>
            </div>
            {action === "Login" && (
                <div className="forgot_password" onClick={() => setShowLostPassword(true)}>Lost Password<span>Click Here</span></div>
            )}
            <div className="submit_container">
                <div 
                    className={action === "Login" ? "submit gray" : "submit"}
                    onClick={() => setAction("Sign Up")}
                >
                    Sign Up
                </div>
                <div 
                    className={action === "Sign Up" ? "submit gray" : "submit"}
                    onClick={() => setAction("Login")}
                >
                    Login
                </div>
            </div>
        </div>
    );
};

export default LoginSignup;
