import React from 'react';
import './StudySync.css';

const StudySync = () => {
  const createFlashcards = () => {
    alert("Your personalized flashcards have been created!");
  }

  const createQuiz = () => {
    alert("Your engaging quiz has been created!");
  }

  return (
    <div className="study-sync-container">
      <h1 className="study-sync-title">Welcome to StudySync</h1>
      <p className="study-sync-description">Transform your lectures effortlessly with StudySync. Upload your recordings and receive concise, structured notes for accelerated learning. Revolutionize your study sessions today!</p>
      <div className="study-sync-features">
        <div className="study-sync-feature">
          <h2 className="study-sync-feature-title">Personalized Flashcards</h2>
          <p className="study-sync-feature-description">Experience the future of education with StudySync. Seamlessly convert lecture recordings into personalized flashcards, tailored to boost comprehension and retention. Unleash the power of interactive learning at your fingertips.</p>
          <button className="study-sync-feature-button" onClick={createFlashcards}>Create Flashcards</button>
        </div>
        <div className="study-sync-feature">
          <h2 className="study-sync-feature-title">Engaging Quizzes</h2>
          <p className="study-sync-feature-description">Turn your lectures into interactive learning! Upload your recordings and let our website transform them into custom questions for an engaging study experience. Unlock knowledge effortlessly with our innovative approach.</p>
          <button className="study-sync-feature-button" onClick={createQuiz}>Create Quiz</button>
        </div>
      </div>
    </div>
  );
};

export default StudySync;
