import React from "react";
import Doctor from "../Assets/doctor-book-appointment.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleCheck,
  faCalendarCheck,
} from "@fortawesome/free-solid-svg-icons";
import { useNavigate  } from "react-router-dom";
import "../Styles/BookAppointment.css";

function BookAppointment() {
  const navigate = useNavigate();

  const handleBookAppointmentClick = () => {
    navigate("/appointment");
  };

  return (
    <div className="ba-section">
      <div className="ba-image-content">
        <img src={Doctor} alt="Doctor Group" className="ba-image1" />
      </div>

      <div className="ba-text-content">
        <h3 className="ba-title">
          <span>Why Choose Learn Loom</span>
        </h3>
        <p className="ba-description">

    Empower Your Learning Experience! With Just One Click, 
    Upload Your Lecture Recording and Watch as Learn Loom Transforms it into 
    Comprehensive Study Material,
    Ready to Elevate Your Understanding and Mastery of the Subject.
        </p>

        <p className="ba-checks ba-check-first">
          <FontAwesomeIcon icon={faCircleCheck} style={{ color: "#1E8FFD" }} /> Efficiency:
            <p1> Say goodbye to tedious note-taking and manual flashcard creation.
             Learn Loom streamlines the process, giving you more time to focus 
             on mastering your subjects.</p1>
        </p>
        <p className="ba-checks">
          <FontAwesomeIcon icon={faCircleCheck} style={{ color: "#1E8FFD" }} />Effectiveness:
          <p1> Our AI-powered platform ensures that your study materials are tailored to your lecture content,
             maximizing retention and comprehension.</p1>
        </p>
        <p className="ba-checks">
          <FontAwesomeIcon icon={faCircleCheck} style={{ color: "#1E8FFD" }} /> Flexibility:
          <p1>Access your study materials anytime, anywhere, on any device. Whether you're at home, in the library,
             or on the go, Learn Loom is your ultimate study companion.</p1>
       </p>

        <button
          className="text-appointment-btn"
          type="button"
          onClick={handleBookAppointmentClick}
        >
          <FontAwesomeIcon icon={faCalendarCheck} /> Upload the recording
        </button>
      </div>
    </div>
  );
}

export default BookAppointment;
