import React, { useState } from 'react';
import '../Styles/UploadVideo.css';

function UploadVideo() {
  const [files, setFiles] = useState([]);

  const onDrop = (e) => {
    e.preventDefault();
    let uploadedFiles = [...e.dataTransfer.files];
    setFiles(uploadedFiles);
  };

  const onBrowse = (e) => {
    let selectedFiles = [...e.target.files];
    setFiles(selectedFiles);
  };

  return (
    <div className="upload-container">
      <div className="upload-box" onDrop={onDrop} onDragOver={(e) => e.preventDefault()}>
        <div className="icon"></div>
        Drag & drop Recording or <input type="file" onChange={onBrowse} /> Browse
        <p>Supported formats: MP3, MP4</p>
      </div>

      {files.length > 0 && (
        <>
          <div className="uploading-files">
            Uploading - {files.length}/{files.length} files
            {files.map((file, index) => (
              <p key={index}>{file.name}</p>
            ))}
          </div>

          <button className="upload-btn">UPLOAD RECORDING</button>
        </>
      )}
    </div>
  );
}

export default UploadVideo;
