import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUpload } from "@fortawesome/free-solid-svg-icons";
import SolutionStep from "./SolutionStep";
import "../Styles/About.css";

function About() {
  const handleUploadClick = () => {
    // Handle the upload click event here
    console.log("Upload button clicked");
  };

  return (
    <div className="about-section" id="about">
      <div className="about-text-content">
        <h4 className="about-titleup"> 
          "Simplify. Master. Excel."
        </h4>
        <h3 className="about-title">
          <span>One Study Tool to Rule Them All</span>
        </h3>
        <p className="about-description">
          Discover the ultimate study companion that sets the standard for academic success. Our all-in-one platform is designed to revolutionize your learning experience. 
        </p>
        <button
          className="text-appointment-btn"
          type="button"
          onClick={handleUploadClick}
        >
          <FontAwesomeIcon />*icon={faUpload} /> UPLOAD
        </button>

        <h4 className="about-text-title">Here's how:</h4>

        <SolutionStep
          title="Upload"
          description="Begin by uploading your lecture recording to our user-friendly platform. With just a few clicks, you'll initiate the transformation process, setting the stage for a personalized learning experience"
        />

        <SolutionStep
          title="Transform"
          description="Behind the scenes, Learn Loom's cutting-edge algorithms spring into action. By harnessing the power of artificial intelligence and natural language processing, we meticulously analyze your lecture content, extracting key insights and essential information."
        />

        <SolutionStep
          title="Generate"
          description="With the analysis complete, Learn Loom works its magic, generating a comprehensive suite of study materials tailored to your lecture content. From meticulously crafted lecture notes to interactive flashcards and thought-provoking questions, our platform ensures you have everything you need to succeed."
        />

        <SolutionStep
          title="Engage"
          description="Dive into your personalized study materials and embark on a journey of active learning. Explore concepts, test your knowledge, and deepen your understanding with Learn Loom's engaging and interactive features."
        />
      </div>
    </div>
  );
}

export default About;
